sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values older
	// then the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var comingdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	comingdueNITasks.addQuery('due_date','<=',gs.hoursAgo(-24));
    comingdueNITasks.addQuery('due_date','>',rightNow);
	comingdueNITasks.addQuery('state','!=',3);

	comingdueNITasks.query();
	// Write a log message for each overdue NeedIt Task Record
	while(comingdueNITasks.next()){
		gs.info("Coming Due, but Not Overdue (v2) NeedIt Task record = " + comingdueNITasks.number);
		gs.eventQueue('x_58872_needit.comingdueNeedItTask',comingdueNITasks,comingdueNITasks.number,gs.getUserName());
	}
